import { setTimeout } from "timers";
import BScroll from "better-scroll";
export default {
    name: "Reading",
    data: () => ({
        isLoading: true,
        bScroll: null,
        chapters: []
    }),
    methods: {
        // 小说章节
        async getChapters(id) {
            let res = await this.$http(`/mix-atoc/${id}`);
            res = res.data;
            this.chapters = res.mixToc.chapters;
            console.log(this.chapters);
            this.$nextTick(() => {
                this.initLoading();
            });
        },
        // 小说章节内容
        // http://chapterup.zhuishushenqi.com/chapter/
        initLoading() {
            setTimeout(() => {
                this.isLoading = false;
            }, 1000);
        },
        initBScroll() {
            if (this.bScroll) {
                this.bScroll.refresh();
            } else {
                this.bScroll = new BScroll(this.$refs.wrapper, {
                    probeType: 3,
                    click: true
                });
            }
        }
    },
    created() {
        let { id } = this.$route.query;
        this.getChapters(id);
    },
    mounted() {
        this.initBScroll();
    },
    beforeDestroy() {
        this.bScroll.destroy();
        this.bScroll = null;
    }
};
